import {
  Alert,
  Button,
  InputLabel,
  MenuItem,
  Select,
  TextField,
} from '@mui/material';
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useState } from 'react';
import {
  nameAction,
  dobAction,
  classAction,
} from '../../redux/acitons/studentActions';
import './StudentForm.css';
import axios from 'axios';

export default function StudentForm() {
  const dispatch = useDispatch();

  const [selectValue, setSelectValue] = useState('');

  const studentData = useSelector((state) => state.student);

  const handleName = (e) => {
    dispatch(nameAction(e.target.value));
  };

  const handleDateOfBirth = (e) => {
    dispatch(dobAction(e.target.value));
  };

  const handleStudentClass = (e) => {
    setSelectValue(e.target.value);
    dispatch(classAction(e.target.value));
  };

  const validateInput = () => {
    const { name, dateOfBirth, mainClass } = studentData;
    return name === '' || dateOfBirth === '' || mainClass === '' ? true : false;
  };

  const postStudent = async () => {
    const { name, dateOfBirth, mainClass } = studentData;
    try {
      await axios.post('http://localhost:5000/create-student', {
        name: name,
        dateOfBirth: dateOfBirth,
        mainClass: mainClass,
      });
    } catch (error) {
      return error;
    }
  };

  return (
    <div className="content">
      <InputLabel id="demo-simple-select-label">Students Name</InputLabel>
      <TextField
        onChange={handleName}
        type="text"
        id="standard-basic"
        variant="standard"
      />
      <InputLabel id="demo-simple-select-label">D.O.B</InputLabel>
      <TextField
        onChange={handleDateOfBirth}
        type="text"
        id="standart-basic"
        label="dd/mm/yy"
      />
      <InputLabel id="demo-simple-select-label">Student Class</InputLabel>
      <Select
        value={selectValue}
        onChange={handleStudentClass}
        labelId="demo-simple-select-label"
        id="demo-simple-select"
        label="Student Class">
        <MenuItem value={'it'}>IT</MenuItem>
        <MenuItem value={'math'}>Math</MenuItem>
        <MenuItem value={'literature'}>Literature</MenuItem>
      </Select>
      <Button
        disabled={validateInput()}
        onClick={postStudent}
        variant="contained">
        Create
      </Button>
      <Alert severity="success">This is a success alert â€” check it out!</Alert>
      ); }
    </div>
  );
}
