import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { MenuItem, Select } from '@mui/material';
export default function StudentAvgChart() {
  const [students, setStudents] = useState([]);
  const [chosenStudent, setChosenStudent] = useState('');

  const getStudents = async () => {
    await axios
      .get('http://localhost:5000/students')
      .then((res) => setStudents(res.data));
  };
  const handleChosenStudent = (e) => {
    setChosenStudent(e.target.value);
    console.log(chosenStudent);
  };
  useEffect(() => {
    getStudents();
  }, []);

  return (
    <div>
      <Select
        onChange={handleChosenStudent}
        labelId="demo-simple-select-label"
        id="demo-simple-select"
        value={chosenStudent}
        label="Student">
        {students.map((student) => {
          return <MenuItem value={student.name}>{student.name}</MenuItem>;
        })}
      </Select>
      StudentAvgChart
    </div>
  );
}
