const init = {
  name: '',
  dob: '',
  mainClass: '',
  grade: {
    year: '',
    quarter: '',
    mathGrade: '',
    itGrade: '',
    literatureGrade: '',
  },
};

export const studentReducer = (state = init, action) => {
  switch (action.type) {
    case 'NAME_INPUT': {
      return { ...state, name: action.payload };
    }
    case 'DATE_OF_BIRTH': {
      return { ...state, dob: action.payload };
    }
    case 'CLASS_INPUT': {
      console.log(action.payload);
      return { ...state, mainClass: action.payload };
    }
    default: {
      return { ...state };
    }
  }
};
