const init = {
  year: '',
  quarter: '',
  mathGrade: '',
  itGrade: '',
  literatureGrade: '',
};

const gradeReducer = (state = init, action) => {
  switch (action.type) {
    case 'YEAR_INPUT': {
      return { ...state, year: action.payload };
    }
    case 'QUARTER_INPUT': {
      return { ...state, quarter: action.payload };
    }
    case 'MATH_GRADE_INPUT': {
      return { ...state, mathGrade: action.payload };
    }
    case 'IT_GRADE_INPUT': {
      return { ...state, itGrade: action.payload };
    }
    case 'LITERATURE_GRADE_INPUT': {
      return {
        ...state,
        literatureGrade: action.payload,
      };
    }
    default: {
      return { ...state };
    }
  }
};
