const init = {
  name: '',
  dob: '',
  mainClass: '',
  grade: {
    year: '',
    quarter: '',
    mathGrade: '',
    itGrade: '',
    literatureGrade: '',
  },
};

export const studentReducer = (state = init, action) => {
  switch (action.type) {
    case 'NAME_INPUT': {
      return { ...state, name: action.payload };
    }
    case 'DATE_OF_BIRTH': {
      return { ...state, dob: action.payload };
    }
    case 'CLASS_INPUT': {
      return { ...state, mainClass: action.payload };
    }
    case 'YEAR_INPUT': {
      return { ...state, grade: { ...state.grade, year: action.payload } };
    }
    case 'QUARTER_INPUT': {
      console.log(action.payload);
      return { ...state, grade: { ...state.grade, quarter: action.payload } };
    }
    case 'MATH_GRADE_INPUT': {
      console.log(action.payload);
      return { ...state, grade: { ...state.grade, mathGrade: action.payload } };
    }
    case 'IT_GRADE_INPUT': {
      return { ...state, grade: { ...state.grade, itGrade: action.payload } };
    }
    case 'LITERATURE_GRADE_INPUT': {
      return {
        ...state,
        grade: { ...state.grade, literatureGrade: action.payload },
      };
    }
    default: {
      return { ...state };
    }
  }
};
